import React, { useState } from 'react';

function Home() {
  const [showVideo, setShowVideo] = useState(false);

  const toggleVideoVisibility = () => {
    setShowVideo(!showVideo);
  };

  return (
    <>
      <div style={{ height: '200px', marginLeft: '35%' }} className="kevo"></div>
      <div style={{ height: '200px',display: 'flex', justifyContent: 'center'}} className="kevio"></div>
      <div style={{ textAlign: 'center' }}>
        <h1>Kevin Thomas Duggan</h1>
        <h2>Computer Software Engineer</h2>

        <div style={{ display: 'inline-block'}}className="custom-checkbox">
          <input
            id="status"
            type="checkbox"
            name="status"
            checked={showVideo}
            onChange={toggleVideoVisibility}
          />
          <label htmlFor="status">
            <div
              className={`status-switch ${showVideo ? 'checked' : ''}`}
              data-unchecked="On"
              data-checked="Off"
            ></div>
          </label>
        </div><br></br>
        {showVideo && (
          <iframe
            src="https://www.loom.com/embed/a5e33ab39fb040bea9ed716f4ac4fc9f?sid=c706cf97-5b76-43cc-a3f2-e264e8b4530f"
            frameBorder="0"
            allowFullScreen
            title="kevin"
            style={{ borderRadius: '25%', width: '750px', height: '400px', border: 'orange 5px solid',  }}
          ></iframe>
        )}


      </div>


    </>
  );
}

export default Home;
