import React, { useState } from 'react';

function Home() {
  const [showVideo, setShowVideo] = useState(false);

  const handleCheckboxChange = (event) => {
    setShowVideo(event.target.checked);
  };

    return (
        <>
        <div style={{height:'200px',marginLeft:"35%"}}className="kevo"></div>
        <div style={{ textAlign: 'center' }}>
      <h1>Kevin Thomas Duggan</h1>
      <h2>Computer Software Engineer</h2>
      <label htmlFor="videoCheckbox">
        <input
          type="checkbox"
          id="videoCheckbox"
          checked={showVideo}
          onChange={handleCheckboxChange}
        />
        Learn More
      </label>

      {showVideo && (
        <iframe
          src="https://www.loom.com/embed/a5e33ab39fb040bea9ed716f4ac4fc9f?sid=c706cf97-5b76-43cc-a3f2-e264e8b4530f"
          frameBorder="0"
          allowFullScreen
          title="kevin"
          style={{ borderRadius: '25%', width: '250px', height: '140px', border: 'orange 5px solid' }}
        ></iframe>
      )}
    </div>

      <div style={{height:'200px',marginLeft:"35%"}}className="kevio"></div>
      </>
    );
  }

  export default Home;
